import React from 'react';
import { connect } from 'react-redux';
import { Field, reduxForm } from 'redux-form';
import { Container, Form, Button, Message, Input } from 'semantic-ui-react';
import { authenticatedSubmitMaxes } from '../../actions/maxes';

class Maxes extends React.Component {
		renderError ({error, touched}) {
			if (touched && error) {
				return (
					<Message error content={error} />
				);
			}
		}

		renderSquatInput = ({ input, label, meta }) => {
			const className = `field ${meta.error && meta.touched ? 'error' : ''}`
				return (
					<div className={className}>
						<label>{label}</label>
						<Form.Input {...input} defaultValue={this.props.squat}/>
						{this.renderError(meta)}
					</div>
				)
		}

		renderBenchInput = ({ input, label, meta }) => {
			const className = `field ${meta.error && meta.touched ? 'error' : ''}`
				return (
					<div className={className}>
						<label>{label}</label>
						<Form.Input {...input} autoComplete="off" value={this.props.bench}/>
						{this.renderError(meta)}
					</div>
				)
		}

		renderDeadliftInput = ({ input, label, meta }) => {
			const className = `field ${meta.error && meta.touched ? 'error' : ''}`
				return (
					<div className={className}>
						<label>{label}</label>
						<Form.Input {...input} autoComplete="off" value={this.props.deadlift}/>
						{this.renderError(meta)}
					</div>
				)
		}

		renderOhpInput = ({ input, label, meta }) => {
			const className = `field ${meta.error && meta.touched ? 'error' : ''}`
				return (
					<div className={className}>
						<label>{label}</label>
						<Form.Input {...input} autoComplete="off" value={this.props.ohp}/>
						{this.renderError(meta)}
					</div>
				)
		}

		renderPowercleanInput = ({ input, label, meta }) => {
			const className = `field ${meta.error && meta.touched ? 'error' : ''}`
				return (
					<div className={className}>
						<label>{label}</label>
						<Form.Input {...input} autoComplete="off" value={this.props.powerclean}/>
						{this.renderError(meta)}
					</div>
				)
		}

		renderInput = ({ input, label, meta }) => {
			const className = `field ${meta.error && meta.touched ? 'error' : ''}`
				return (
					<div className={className}>
						<label>{label}</label>
						<Form.Input {...input} autoComplete="off" />
						{this.renderError(meta)}
					</div>
				)
		}

		onSubmit = (formValues) => {
			const { squat, bench, deadlift, ohp, powerclean } = formValues
			this.props.authenticatedSubmitMaxes(squat, bench, deadlift, ohp, powerclean, this.props.history);
		}

	render () {
		if (this.props.authenticated === true) {
			return (
				<Container>
				<h1>Enter Your 1 Rep Maxes</h1>
					<div>
						<Form onSubmit={this.props.handleSubmit(this.onSubmit)} error>
							<Field name="squat" component={this.renderSquatInput} label="Squat" /> 
							<Field name="bench" component={this.renderBenchInput} label="Bench" /> 
							<Field name="deadlift" component={this.renderDeadliftInput} label="Deadlift" />
							<Field name="ohp" component={this.renderOhpInput} label="OHP" /> 
							<Field name="powerclean" component={this.renderPowercleanInput} label="Power Clean" />  
							<Button>Submit</Button>
						</Form>
					</div>
				</Container>
			);
		} else {
			return (
				<Container>
				<h1>Enter Your 1 Rep Maxes</h1>
					<div>
						<Form onSubmit={this.props.handleSubmit(this.onSubmit)} error>
							<Field name="squat" component={this.renderInput} label="Squat" /> 
							<Field name="bench" component={this.renderInput} label="Bench" /> 
							<Field name="deadlift" component={this.renderInput} label="Deadlift" />
							<Field name="ohp" component={this.renderInput} label="OHP" /> 
							<Field name="powerclean" component={this.renderInput} label="Power Clean" />  
							<Button>Submit</Button>
						</Form>
					</div>
				</Container>
			);
		}
	}
};

const validate = (formValues) => {
	const errors = {};

	if (!formValues.squat) {
		errors.squat = 'Please enter your squat max.';
	}

	if (!formValues.bench) {
		errors.bench = 'Please enter your bench max.';
	}

	if (!formValues.deadlift) {
		errors.deadlift = 'Please enter your deadlift max.';
	}

	if (!formValues.ohp) {
		errors.ohp = 'Please enter your ohp max.';
	}

	if (!formValues.powerclean) {
		errors.powerclean = 'Please enter your powerclean max.';
	}

	return errors;
};

const mapStateToProps = (state) => {
	return {
		authenticated: state.signIn.authenticated,
		userId: state.signIn.userId,
		squat: state.signIn.squat,
		bench: state.signIn.bench,
		deadlift: state.signIn.deadlift,
		ohp: state.signIn.ohp,
		powerclean: state.signIn.powerclean
	}
}

const formWrapped = reduxForm({
	form: 'maxForm',
	touchOnBlur: false,
	validate
})(Maxes)

export default connect(mapStateToProps, { authenticatedSubmitMaxes })(formWrapped);
