import axios from 'axios';

export function register (registerName, registerEmail, registerPassword, history){
	return (dispatch) => {
			 axios({
				method: 'post',
				url: 'http://localhost:3000/register',
				data:{
					name: registerName,
					email: registerEmail,
					password: registerPassword
				}
			})
			.then(user => {
				if (user) {
					dispatch({type: 'REGISTERED', payload: user})
					history.push('/');
				}
			})
		} 
	}
