import React from 'react';
import { connect } from 'react-redux';
import { Field, reduxForm } from 'redux-form';
import { Grid, Container, Form, Button, Message } from 'semantic-ui-react';
import { authenticatedSubmitMaxes, unauthenticatedSubmitMaxes } from '../../actions/maxes';

class Maxes extends React.Component {
		renderError ({error, touched}) {
			if (touched && error) {
				return (
					<Message error content={error} />
				);
			}
		}

		renderInput = ({ input, label, meta }) => {
			const className = `field ${meta.error && meta.touched ? 'error' : ''}`
				return (
					<div className={className}>
					<Grid>
						<Grid.Row>
							<Grid.Column width={3}>
								<label>{label}</label>
							</Grid.Column>
							<Grid.Column width={3}>
								<Form.Input {...input} autoComplete="off" />
							</Grid.Column>
						</Grid.Row>
					</Grid>
						{this.renderError(meta)}
					</div>
				)
		}

		renderRadioTwoFive = ({ input, lable, meta }) => {
			const className = `field ${meta.error && meta.touched ? 'error' : ''}`
			return (
				<div className={className}>
					<Form.Radio {...input} value="2.5"/>
					{this.renderError(meta)}
				</div>
			)
		}

		onSubmitAuthenticated = (formValues) => {
			const { squat, bench, deadlift, ohp, powerclean } = formValues
			this.props.authenticatedSubmitMaxes(this.props.userId, squat, bench, deadlift, ohp, powerclean, this.props.goal, this.props.history);
		}

		onSubmitUnauthenticated = (formValues) => {
			const { squat, bench, deadlift, ohp, powerclean } = formValues
			this.props.unauthenticatedSubmitMaxes(squat, bench, deadlift, ohp, powerclean, this.props.goal, this.props.history);
		}

	render () {
		if (this.props.authenticated === true) {
			return (
				<Container>
				<h1>Enter Your 1 Rep Maxes</h1>
					<div>
						<Form onSubmit={this.props.handleSubmit(this.onSubmitAuthenticated)} error>
							<Field name="squat" component={this.renderInput} label="Squat" /> 
							<Field name="bench" component={this.renderInput} label="Bench" /> 
							<Field name="deadlift" component={this.renderInput} label="Deadlift" />
							<Field name="ohp" component={this.renderInput} label="OHP" /> 
							<Field name="powerclean" component={this.renderInput} label="Power Clean" /> 
							<Field name="squatIncrement" component={this.renderRadioTwoFive} />
							<Button>Submit</Button>
						</Form>
					</div>
				</Container>
			);
		} else {
			return (
				<Container>
				<h1>Enter Your 1 Rep Maxes</h1>
					<div>
						<Form onSubmit={this.props.handleSubmit(this.onSubmitUnauthenticated)} error>
						<Grid>
							<Grid.Row verticalAlign={'middle'}>
								<Grid.Column>
									<Field name="squat" component={this.renderInput} label="Squat" /> 
								</Grid.Column>
								<Grid.Column>
									<Field name="squatIncrement" component={this.renderRadioTwoFive} /> 
								</Grid.Column>
								<Grid.Column>
									<Field name="squatIncrement" component={this.renderRadioTwoFive} /> 
								</Grid.Column>
								<Grid.Column>
									<Field name="squatIncrement" component={this.renderRadioTwoFive} /> 
								</Grid.Column>
							</Grid.Row>
							<Grid.Row verticalAlign={'middle'}>
								<Grid.Column>
									<Field name="bench" component={this.renderInput} label="Bench" /> 
								</Grid.Column>
								<Grid.Column>
									<Field name="benchIncrement" component={this.renderRadioTwoFive} /> 
								</Grid.Column>
								<Grid.Column>
									<Field name="benchIncrement" component={this.renderRadioTwoFive} /> 
								</Grid.Column>
								<Grid.Column>
									<Field name="benchIncrement" component={this.renderRadioTwoFive} /> 
								</Grid.Column>
							</Grid.Row>
							<Grid.Row verticalAlign={'middle'}>
								<Grid.Column>
									<Field name="deadlift" component={this.renderInput} label="Deadlift" /> 
								</Grid.Column>
								<Grid.Column>
									<Field name="deadliftIncrement" component={this.renderRadioTwoFive} /> 
								</Grid.Column>
								<Grid.Column>
									<Field name="deadliftIncrement" component={this.renderRadioTwoFive} /> 
								</Grid.Column>
								<Grid.Column>
									<Field name="deadliftIncrement" component={this.renderRadioTwoFive} /> 
								</Grid.Column>
							</Grid.Row>
							<Grid.Row verticalAlign={'middle'}>
								<Grid.Column>
									<Field name="ohp" component={this.renderInput} label="OHP" /> 
								</Grid.Column>
								<Grid.Column>
									<Field name="ohpIncrement" component={this.renderRadioTwoFive} /> 
								</Grid.Column>
								<Grid.Column>
									<Field name="ohpIncrement" component={this.renderRadioTwoFive} /> 
								</Grid.Column>
								<Grid.Column>
									<Field name="ohpIncrement" component={this.renderRadioTwoFive} /> 
								</Grid.Column>
							</Grid.Row>
								<Grid.Row verticalAlign={'middle'}>
								<Grid.Column>
									<Field name="powerclean" component={this.renderInput} label="Power Clean" /> 
								</Grid.Column>
								<Grid.Column>
									<Field name="powercleanIncrement" component={this.renderRadioTwoFive} /> 
								</Grid.Column>
								<Grid.Column>
									<Field name="powercleanIncrement" component={this.renderRadioTwoFive} /> 
								</Grid.Column>
								<Grid.Column>
									<Field name="powercleanIncrement" component={this.renderRadioTwoFive} /> 
								</Grid.Column>
							</Grid.Row>
						</Grid>
							<Button>Submit</Button>
						</Form>
					</div>
				</Container>
			);
		}
	}
};


const validate = (formValues) => {
	const errors = {};

	if (!formValues.squat) {
		errors.squat = 'Please enter your squat max.';
	}

	if (!formValues.bench) {
		errors.bench = 'Please enter your bench max.';
	}

	if (!formValues.deadlift) {
		errors.deadlift = 'Please enter your deadlift max.';
	}

	if (!formValues.ohp) {
		errors.ohp = 'Please enter your ohp max.';
	}

	if (!formValues.powerclean) {
		errors.powerclean = 'Please enter your powerclean max.';
	}

	return errors;
};

const mapStateToProps = (state, props) => {
	return {
		authenticated: state.signIn.authenticated,
		goal: state.goals.goal,
		userId: state.signIn.userId,
		initialValues: {
			squat: state.signIn.squat,
			bench: state.signIn.bench,
